command:
/*

This Macro will compile all reroll stats for actors and create a journal entry named "Reroll tracker Stats" in the base Journal directory.

*/
if (game.user?.isGM) {
  const api = game.modules.get("pf2e-reroll-stats")?.api ?? globalThis.pf2eRerollStats;
  if (api?.compileActorStatsToJournal) {
    api.compileActorStatsToJournal();
    // Replace "Your Journal Name" with the name of the journal entry
    const journalName = "Reroll Stats";

    // Find the journal entry by name
    const journal = game.journal.contents.find(j => j.name === journalName);

    // If the journal entry exists, show it
    if (journal) {
      journal.sheet.render(true);
    } else {
      console.error(`Journal entry with name "${journalName}" not found.`);
    }
  } else {
    ui.notifications.error("ReRoll Stats module API not available.");
  }
} else {
  ui.notifications.warn("Only a GM can open the Reroll Editor.");
}
