command:
/*

This macro will DELETE all re-roll data for selected token actor! If you want to just reset all stats to 0, then usee the Reset macro instead. 

!!! CAN NOT BE UNDONE !!!

*/
if (game.user?.isGM) {
  const api = game.modules.get("pf2e-reroll-stats")?.api ?? globalThis.pf2eRerollStats;
  if (api?.openRerollEditor) {
    api.deleteActorRollData();
  } else {
    ui.notifications.error("ReRoll Stats module API not available.");
  }
} else {
  ui.notifications.warn("Only a GM can open the Reroll Editor.");
}
